import UIKit

class ProductDetailRouter: ProductDetailWireframeProtocol {

    weak var viewController: UIViewController?

    static func createModule(with product: ProductEntity) -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = ProductDetailViewController(userInterface: ProductDetailView())
        let interactor = ProductDetailInteractor(product: product)
        let router = ProductDetailRouter()
        let presenter = ProductDetailPresenter(interface: view, interactor: interactor, router: router)

        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view

        return view
    }
}
